mapper.select:
SELECT*
FROM ( SELECT inner_table.*, ROWNUM AS row_seq
				FROM ( SELECT *
								FROM blackList
							
								
											
		
												
									 				ban_reason = #{searchKeyword}
												
												
									 			  black_check_code = #{searchKeyword}
												
											
										
										ORDER BY black_no ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT *
					FROM  blackList
				
					
						
							
					 			ban_reason = #{searchKeyword}
							
							
					 		 	 black_check_code = #{searchKeyword}
							
						
					 ) countTable
