mapper.select:
SELECT*
FROM ( SELECT inner_table.*, ROWNUM AS row_seq
				FROM ( SELECT b.black_no, b.user_no, b.ban_reason, b.ban_start_day, b.ban_expire_day, u.black_code
								FROM blackList b, users u
								WHERE b.user_no=u.user_no and u.black_code ='2'
								
											
		
												
									 				 and user_name = #{searchKeyword}
												
												
									 			    and delete_count = #{searchKeyword}
												
											
										
										ORDER BY b.black_no ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT b.black_no, b.user_no, b.ban_reason, b.ban_start_day, b.ban_expire_day, u.black_code
					FROM  blackList b, users u
					WHERE u.black_code ='2'
					
					
							
					 			and user_name = #{searchKeyword}
							
							
					 		    and delete_count = #{searchKeyword}
							
					
					 ) countTable
