mapper.update:
UPDATE users
user_name = #{userName:VARCHAR} , user_nickname = #{userNickname:VARCHAR} , user_birth = #{userBirth:INTEGER}, user_addr = #{userAddr:VARCHAR}, profile = #{profile:VARCHAR}, black_code = #{blackCode:VARCHAR}
WHERE user_no = #{userNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT user_no , user_name , sns_no, reg_date, delete_count
										FROM users
										
											
												
									 				user_name = #{searchKeyword}
												
												
									 				delete_count = #{searchKeyword}
												
											
										
										ORDER BY user_no ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT user_no , user_name , reg_date, delete_count
										FROM users
			     						WHERE black_code = '1'
													
										
											
												
									 				 and user_name = #{searchKeyword}
												
												
												
									 			    and delete_count = #{searchKeyword}
												
									
										
										ORDER BY user_no ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT user_no , user_name , reg_date, delete_count
					FROM users
					WHERE black_code = '1'
					
					
							
					 			user_name = #{searchKeyword}
							
							
					 			delete_count = #{searchKeyword}
							
					
					 ) countTable

  	SELECT COUNT(*)
  	FROM(	SELECT user_no , user_name , sns_no, reg_date, delete_count
					FROM users
					
						
							
					 			user_name = #{searchKeyword}
							
							
					 			delete_count = #{searchKeyword}
							
						
					 ) countTable

SELECT NVL(COUNT(*), 0)
FROM users
WHERE user_nickname = #{userNickname}
